const React = require('react');
const DefaultLayout = require('../layout/default');

class Show extends React.Component {
  render() {
    const { apod } = this.props;

    if (!apod) {
      return (
        <DefaultLayout title="APOD Show Page">
          <p>Invalid APOD ID</p>
          <a href="/apods">Back to APOD Index</a>
        </DefaultLayout>
      );
    }

    return (
      <DefaultLayout title={`APOD: ${apod.title}`}>
        <h1>{apod.title}</h1>
        <p>{apod.description}</p>
        <img src={apod.imageURL} alt={apod.title} />
        <p>Date: {new Date(apod.date).toDateString()}</p>
        <a href="/apods">Back to APOD Index</a>
      </DefaultLayout>
    );
  }
}

module.exports = Show;
