import React, { useState, useEffect } from 'react';
import ApodList from './ApodList';
import axios from 'axios';

const ApodViewer = () => {
  const [apodData, setApodData] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [newEntryData, setNewEntryData] = useState({ title: '', date: '', description: '', imageUrl: '' });
  const [apodEntries, setApodEntries] = useState([]);

  // Use useEffect to fetch APOD entries when the component mounts
  useEffect(() => {
    fetchApodEntries();
  }, []);

  const fetchApodData = async (date) => {
    try {
      const response = await axios.get(`http://localhost:3002/api/apod/apod/${date}`); // Use the correct URL path
      console.log('Fetched APOD data:', response.data);
      setApodData(response.data);
    } catch (error) {
      console.error('Error fetching APOD data:', error);
    }
  };

  const onSearch = async (searchTerm) => {
    try {
      // Perform actions related to searching, e.g., making an API request
      // For demonstration purposes, we'll log the search term
      console.log('Searching for:', searchTerm);

      // You can make an API request to fetch search results and update setSearchResults
      // Example: const response = await axios.get(`/api/search?query=${searchTerm}`);
      // Update setSearchResults with the response data
      // setSearchResults(response.data);
    } catch (error) {
      console.error('Error searching APOD data:', error);
      // Handle the error, e.g., show an error message
    }
  };

  const handleSearch = async (event) => {
    event.preventDefault();

    try {
      console.log('Search Term:', searchTerm);
      const response = await axios.get(
        `http://localhost:3002/api/apod?date=${encodeURIComponent(searchTerm)}`
      );
      console.log('Search Response Data:', response.data);
      setSearchResults([response.data]);
     
    } catch (error) {
      console.error('Error searching APOD data:', error);
    }
  };

  const createApodEntry = async () => {
    try {
      const response = await axios.post('http://localhost:3002/api/apod', newEntryData);
      console.log('Created APOD entry:', response.data);
      // You can update the UI or reset the form after successful creation
    } catch (error) {
      console.error('Error creating APOD entry:', error);
    }
  };

  const fetchApodEntries = async () => {
    try {
      const response = await axios.get('http://localhost:3002/api/apod');
      setApodEntries(response.data); // Update apodEntries state with fetched data
    } catch (error) {
      console.error(error);
    }
  };

  const updateApodEntry = async (entryId, updatedData) => {
    try {
      const response = await axios.put(`/apod/${entryId}`, updatedData);
      // Handle successful update (e.g., display a success message)
    } catch (error) {
      console.error(error);
    }
  };

  const deleteApodEntry = async (entryId) => {
    try {
      const response = await axios.delete(`/apod/${entryId}`);
      // Handle successful deletion (e.g., display a success message)
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="apod-container">
      {apodData ? (
         <div>
         <h1>{apodData.title}</h1>
         <img src={apodData.imageUrl} alt={apodData.title} />
         <p>{apodData.description}</p>
       </div>
      ) : null}

      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Enter date (YYYY-MM-DD)"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>

      {apodEntries && apodEntries.length > 0 ? (
        <ApodList apodEntries={apodEntries} />
      ) : (
        <p>Loading...</p>
      )}

      <form onSubmit={createApodEntry}>
        <input
          type="text"
          placeholder="Title"
          value={newEntryData.title}
          onChange={(e) => setNewEntryData({ ...newEntryData, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Date"
          value={newEntryData.date}
          onChange={(e) => setNewEntryData({ ...newEntryData, date: e.target.value })}
        />
        <input
          type="text"
          placeholder="Description"
          value={newEntryData.description}
          onChange={(e) => setNewEntryData({ ...newEntryData, description: e.target.value })}
        />
        <input
          type="text"
          placeholder="Image URL"
          value={newEntryData.imageUrl}
          onChange={(e) => setNewEntryData({ ...newEntryData, imageUrl: e.target.value })}
        />
        <button type="submit">Create</button>
      </form>

      {searchResults.length > 0 ? (
  <div>
    <h2>Search Results</h2>
    {searchResults.map((result, index) => (
      <div key={result._id || index}>
        <h3>{result.title}</h3>
        <img src={result.imageUrl} alt={result.title} />
        <p>{result.description}</p>
      </div>
          ))}
        </div>
      ) : null}
    </div>
  );
};

export default ApodViewer;
