import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ApodViewer = () => {
  const [apodData, setApodData] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [newEntryData, setNewEntryData] = useState({ title: '', date: '', description: '', imageUrl: '' });

  const fetchApodData = async (date) => {
    try {
      const response = await axios.get(
        `http://localhost:3001/api/apod?date=${date}` // Update with your backend URL
      );
      console.log('Fetched APOD data:', response.data);
      setApodData(response.data);
    } catch (error) {
      console.error('Error fetching APOD data:', error);
    }
  };

  const handleSearch = async (event) => {
    event.preventDefault();

    try {
      console.log('Search Term:', searchTerm);
      const response = await axios.get(
        `http://localhost:3001/api/apod?date=${encodeURIComponent(searchTerm)}`
      );
      console.log('Search Response Data:', response.data);
      setSearchResults([response.data]);
    } catch (error) {
      console.error('Error searching APOD data:', error);
    }
  };

  const createApodEntry = async () => {
    try {
      const response = await axios.post('/apod', newEntryData); // Send newEntryData to create a new entry
      console.log('Created APOD entry:', response.data);
      // You can update the UI or reset the form after successful creation
    } catch (error) {
      console.error('Error creating APOD entry:', error);
    }
  };

  return (
    <div className="apod-container">
      {apodData ? (
        <div>
          <h1>{apodData.title}</h1>
          <img src={apodData.url} alt={apodData.title} />
          <p>{apodData.explanation}</p>
        </div>
      ) : null}

      {/* Search Bar */}
      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Enter date (YYYY-MM-DD)"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>

      {/* Search Results */}
      {searchResults.length > 0 ? (
        <div>
          <h2>Search Results</h2>
          {searchResults.map((result, index) => (
            <div key={result.id || index}>
              <h3>{result.title}</h3>
              <img src={result.url} alt={result.title} />
              <p>{result.explanation}</p>
            </div>
          ))}
        </div>
      ) : null}
    </div>
  );
};

export default ApodViewer;
